import "./AtividadesFormModal.css"
import Modal from "components/Modal/Modal";

function AtividadesFormModal({ atividade, closeModal }) {
    return (
      <Modal closeModal={closeModal}>
        <div className="PaletaDetalhesModal">
          
          <form method="post" action="http://localhost:4000/atividades">

            <input type="text" name="titulo"/>

            <input type="text"  name="descricao"/>
           
            <input type="text"  name="data"/>

            <input type="text"  name="hora"/>

            <input type="submit"  value={"criar"}/>

          </form>

        </div>
      </Modal>
    );
  }
  
  export default AtividadesFormModal;
