import React, { useEffect, useState } from "react";
import "./ListaAtividades.css";
import ListaAtividadeItem from "components/ListaAtividadesItem/ListaAtividadesItem";
import { api } from "utils/api";
import AtividadesFormModal from "components/AtividadesFormModal/AtividadesFormModal";



function AtividadeLista() {

    const [atividades, setAtividades] = useState([]);

    const getAtividades = async () => {
        const response = await api.getAllAtividades()
        setAtividades(response)
    }

    useEffect(() => {
        getAtividades();
    }, [])

    const [atividadeModal, setAtividadeModal] = useState([]);


    {atividadeModal && <AtividadesFormModal atividade={atividadeModal} closeModal={() => setAtividadeModal(false)} />}

    return (

        <div className="AtividadeLista">

            <button onClick={(atividadeid) => setAtividadeModal(atividade) }>  </button>
            {atividades.map((atividade, index) => (

                <ListaAtividadeItem
                    key={`AtividadeListaItem-${index}`}
                    atividade={atividade}
                />
            ))}



            <AtividadesFormModal
            />
        </div>

    );
}

export default AtividadeLista;
