import React, { useState } from "react";
import "./ListaAtividades.css";
import { atividades } from "data/db.js";




function AtividadeLista() {

    return (
        <div className="AtividadeLista">
            {atividades.map((atividade, index) => (

                <div className="row">
                    <div className="AtividadeListaItem" key={`AtividadeListaItem-${index}`}>
                        <div>

                            <p>
                                <input className="checkbox" type="checkbox" name="colors" value="blue" /><br />

                            </p>

                            <div className="AtividadeListaItem__titulo">
                                {atividade.nome}
                            </div>

                            <div className="AtividadeListaItem__preco">{`R$ ${atividade.hora.toFixed(2)}`}</div>

                            <div className="AtividadeListaItem__descricao">
                                {atividade.descricao}
                            </div>

                            <div className="AtividadeListaItem__data">
                                {atividade.data}
                            </div>

                            <div className="AtividadeListaItem__hora">
                                {atividade.hora}
                            </div>

                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default AtividadeLista;
